---Finding Collateral Code
SELECT CC.ID,
       CC.CODE_TX,
       CC.DESCRIPTION_TX
FROM dbo.COLLATERAL_CODE CC
    INNER JOIN dbo.LCCG_COLLATERAL_CODE_RELATE CCR
        ON CCR.COLLATERAL_CODE_ID = CC.ID
    INNER JOIN dbo.LENDER_COLLATERAL_CODE_GROUP LCCG
        ON LCCG.ID = CCR.LCCG_ID
    INNER JOIN dbo.LENDER L
        ON L.ID = LCCG.LENDER_ID
WHERE L.CODE_TX = '4750' AND CC.CODE_TX = 'Vehicle'

--525




SELECT L.NUMBER_TX, CC.DESCRIPTION_TX,
       C.* 
FROM LOAN L
    INNER JOIN COLLATERAL C
        ON L.ID = C.LOAN_ID
    INNER JOIN dbo.COLLATERAL_CODE CC
        ON CC.ID = C.COLLATERAL_CODE_ID
    INNER JOIN dbo.LENDER LL
        ON LL.ID = L.LENDER_ID
WHERE LL.CODE_TX IN ( '4750' )
      AND C.ID IN (SELECT ID FROM  UniTracHDStorage..INC0354577)